def _initialize(self, X, y, init):
    """Initialize the transformation.

    Parameters
    ----------
    X : array-like, shape (n_samples, n_features)
        The training samples.

    y : array-like, shape (n_samples,)
        The training labels.

    init : string or numpy array of shape (n_features_a, n_features_b)
        The validated initialization of the linear transformation.

    Returns
    -------
    transformation : array, shape (n_components, n_features)
        The initialized linear transformation.

    """

    transformation = init
    if self.warm_start and hasattr(self, 'components_'):
        transformation = self.components_
    elif isinstance(init, np.ndarray):
        pass
    else:
        n_samples, n_features = X.shape
        n_components = self.n_components or n_features
        if init == 'auto':
            n_classes = len(np.unique(y))
            if n_components <= min(n_features, n_classes - 1):
                init = 'lda'
            elif n_components < min(n_features, n_samples):
                init = 'pca'
            else:
                init = 'identity'
        if init == 'identity':
            transformation = np.eye(n_components, X.shape[1])
        elif init == 'random':
            transformation = self.random_state_.randn(n_components,
                                                      X.shape[1])
        elif init in {'pca', 'lda'}:
            init_time = time.time()
            if init == 'pca':
                pca = PCA(n_components=n_components,
                          random_state=self.random_state_)
                if self.verbose:
                    print('Finding principal components... ', end='')
                    sys.stdout.flush()
                pca.fit(X)
                transformation = pca.components_
            elif init == 'lda':
                from ..discriminant_analysis import (
                    LinearDiscriminantAnalysis)
                lda = LinearDiscriminantAnalysis(n_components=n_components)
                if self.verbose:
                    print('Finding most discriminative components... ',
                          end='')
                    sys.stdout.flush()
                lda.fit(X, y)
                transformation = lda.scalings_.T[:n_components]
            if self.verbose:
                print('done in {:5.2f}s'.format(time.time() - init_time))
    return transformation

def format_signature(self, **kwargs: Any) -> str:
    sigs = []
    if self.analyzer and '.'.join(self.objpath) in self.analyzer.overloads:
        # Use signatures for overloaded functions instead of the implementation function.
        overloaded = True
    else:
        overloaded = False
        sig = super().format_signature(**kwargs)
        sigs.append(sig)

    if inspect.is_singledispatch_function(self.object):
        # append signature of singledispatch'ed functions
        for typ, func in self.object.registry.items():
            if typ is object:
                pass  # default implementation. skipped.
            else:
                self.annotate_to_first_argument(func, typ)

                documenter = FunctionDocumenter(self.directive, '')
                documenter.object = func
                documenter.objpath = [None]
                sigs.append(documenter.format_signature())
    if overloaded:
        __globals__ = safe_getattr(self.object, '__globals__', {})
        for overload in self.analyzer.overloads.get('.'.join(self.objpath)):
            overload = evaluate_signature(overload, __globals__,
                                          self.env.config.autodoc_type_aliases)

            sig = stringify_signature(overload, **kwargs)
            sigs.append(sig)

    return "\n".join(sigs)

def merge_typehints(app: Sphinx, domain: str, objtype: str, contentnode: Element) -> None:
    if domain != 'py':
        return
    if app.config.autodoc_typehints != 'description':
        return
    if objtype == 'class' and app.config.autoclass_content not in ('init', 'both'):
        return

    try:
        signature = cast(addnodes.desc_signature, contentnode.parent[0])
        if signature['module']:
            fullname = '.'.join([signature['module'], signature['fullname']])
        else:
            fullname = signature['fullname']
    except KeyError:
        # signature node does not have valid context info for the target object
        return

    annotations = app.env.temp_data.get('annotations', {})
    if annotations.get(fullname, {}):
        field_lists = [n for n in contentnode if isinstance(n, nodes.field_list)]
        if field_lists == []:
            field_list = insert_field_list(contentnode)
            field_lists.append(field_list)

        for field_list in field_lists:
            modify_field_list(field_list, annotations[fullname])

def get_overloaded_signatures(self) -> List[Signature]:
    if self._signature_class and self._signature_method_name:
        for cls in self._signature_class.__mro__:
            try:
                analyzer = ModuleAnalyzer.for_module(cls.__module__)
                analyzer.parse()
                qualname = '.'.join([cls.__qualname__, self._signature_method_name])
                if qualname in analyzer.overloads:
                    return analyzer.overloads.get(qualname)
            except PycodeError:
                pass

    return []

def _values(self, alpha, y, v, Q, QT_y):
    G_diag, c = self._errors_and_values_helper(alpha, y, v, Q, QT_y)
    return y - (c / G_diag), c

def _load_reporter_by_name(self, reporter_name: str) -> reporters.BaseReporter:
    name = reporter_name.lower()
    if name in self._reporters:
        return self._reporters[name]()

    try:
        reporter_class = _load_reporter_by_class(reporter_name)
    except (ImportError, AttributeError, AssertionError) as e:
        raise exceptions.InvalidReporterError(name) from e
    else:
        return reporter_class()

def _eval_rewrite_as_Sum(self, n, k_sym=None, symbols=None):
    from sympy import Sum
    if (k_sym is not None) or (symbols is not None):
        return self

    # Dobinski's formula
    if not n.is_nonnegative:
        return self
    k = Dummy('k', integer=True, nonnegative=True)
    return 1 / E * Sum(k**n / factorial(k), (k, 0, S.Infinity))

def __new__(cls, e, z, z0, dir="+"):
    e = sympify(e)
    z = sympify(z)
    z0 = sympify(z0)

    if z0 is S.Infinity:
        dir = "-"
    elif z0 is S.NegativeInfinity:
        dir = "+"

    if isinstance(dir, string_types):
        dir = Symbol(dir)
    elif not isinstance(dir, Symbol):
        raise TypeError("direction must be of type basestring or Symbol, not %s" % type(dir))
    if str(dir) not in ('+', '-'):
        raise ValueError(
            "direction must be either '+' or '-', not %s" % dir)

    obj = Expr.__new__(cls)
    obj._args = (e, z, z0, dir)
    return obj

def _get_kernel(self, X, Y=None):
    if callable(self.kernel):
        params = self.kernel_params or {}
    else:
        params = {"gamma": self.gamma,
                  "degree": self.degree,
                  "coef0": self.coef0}
    return pairwise_kernels(X, Y, metric=self.kernel,
                            filter_params=True, n_jobs=self.n_jobs,
                            **params)

def _eval_is_integer(self):
    from sympy.core.logic import fuzzy_and, fuzzy_not
    p, q = self.args
    if fuzzy_and([p.is_integer, q.is_integer, fuzzy_not(q.is_zero)]):
        return True

def _eval_is_nonnegative(self):
    if self.args[1].is_positive:
        return True

def add_url_rule(
    self,
    rule: str,
    endpoint: t.Optional[str] = None,
    view_func: t.Optional[ft.RouteCallable] = None,
    provide_automatic_options: t.Optional[bool] = None,
    **options: t.Any,
) -> None:
    if endpoint is None:
        endpoint = _endpoint_from_view_func(view_func)  # type: ignore
    options["endpoint"] = endpoint
    methods = options.pop("methods", None)

    # if the methods are not given and the view_func object knows its
    # methods we can use that instead.  If neither exists, we go with
    # a tuple of only ``GET`` as default.
    if methods is None:
        methods = getattr(view_func, "methods", None) or ("GET",)
    if isinstance(methods, str):
        raise TypeError(
            "Allowed methods must be a list of strings, for"
            ' example: @app.route(..., methods=["POST"])'
        )
    methods = {item.upper() for item in methods}

    # Methods that should always be added
    required_methods = set(getattr(view_func, "required_methods", ()))

    # starting with Flask 0.8 the view_func object can disable and
    # force-enable the automatic options handling.
    if provide_automatic_options is None:
        provide_automatic_options = getattr(
            view_func, "provide_automatic_options", None
        )

    if provide_automatic_options is None:
        if "OPTIONS" not in methods:
            provide_automatic_options = True
            required_methods.add("OPTIONS")
        else:
            provide_automatic_options = False

    # Add the required methods now.
    methods |= required_methods

    rule = self.url_rule_class(rule, methods=methods, **options)
    rule.provide_automatic_options = provide_automatic_options  # type: ignore

    self.url_map.add(rule)
    if view_func is not None:
        old_func = self.view_functions.get(endpoint)
        if old_func is not None and old_func != view_func:
            raise AssertionError(
                "View function mapping is overwriting an existing"
                f" endpoint function: {endpoint}"
            )
        self.view_functions[endpoint] = view_func

def tag(self, name=None, compile_function=None):
    if name is None and compile_function is None:
        # @register.tag()
        return self.tag_function
    elif name is not None and compile_function is None:
        if callable(name):
            # @register.tag
            return self.tag_function(name)
        else:
            # @register.tag('somename') or @register.tag(name='somename')
            def dec(func):
                return self.tag(name, func)

            return dec
    elif name is not None and compile_function is not None:
        # register.tag('somename', somefunc)
        self.tags[name] = compile_function
        return compile_function
    else:
        raise ValueError(
            "Unsupported arguments to Library.tag: (%r, %r)"
            % (name, compile_function),
        )

def check_for_template_tags_with_the_same_name(app_configs, **kwargs):
    errors = []
    libraries = defaultdict(list)

    for conf in settings.TEMPLATES:
        custom_libraries = conf.get("OPTIONS", {}).get("libraries", {})
        for module_name, module_path in custom_libraries.items():
            libraries[module_name].append(module_path)

    for module_name, module_path in get_template_tag_modules():
        libraries[module_name].append(module_path)

    for library_name, items in libraries.items():
        if len(items) > 1:
            errors.append(
                Error(
                    E003.msg.format(
                        repr(library_name),
                        ", ".join(repr(item) for item in items),
                    ),
                    id=E003.id,
                )
            )

    return errors

def get_template_libraries(self, libraries):
    loaded = {}
    for name, path in libraries.items():
        loaded[name] = import_library(path)
    return loaded

def import_library(name):
    """
    Load a Library object from a template tag module.
    """
    try:
        module = import_module(name)
    except ImportError as e:
        raise InvalidTemplateLibrary(
            "Invalid template library specified. ImportError raised when "
            "trying to load '%s': %s" % (name, e)
        )
    try:
        return module.register
    except AttributeError:
        raise InvalidTemplateLibrary(
            "Module  %s does not have a variable named 'register'" % name,
        )

def contribute_to_related_class(self, cls, related):
    super().contribute_to_related_class(cls, related)
