        if self.flags and not isinstance(self.regex, str):
            raise TypeError("If the flags are set, regex must be a regular expression string.")

        self.regex = _lazy_re_compile(self.regex, self.flags)

    def __call__(self, value):
        """
        Validate that the input contains (or does *not* contain, if
        inverse_match is True) a match for the regular expression.
        """
        regex_matches = self.regex.search(str(value))
        invalid_input = regex_matches if self.inverse_match else not regex_matches
        if invalid_input:
            raise ValidationError(self.message, code=self.code)

    def __eq__(self, other):
        return (
            isinstance(other, RegexValidator) and
            self.regex.pattern == other.regex.pattern and
            self.regex.flags == other.regex.flags and
            (self.message == other.message) and
            (self.code == other.code) and
            (self.inverse_match == other.inverse_match)
        )

def __lt__(self, other):
    from sympy import StrictLessThan
    try:
        other = _sympify(other)
    except SympifyError:
        raise TypeError("Invalid comparison %s < %s" % (self, other))
    for me in (self, other):
        if me.is_complex and me.is_extended_real is False:
            raise TypeError("Invalid comparison of complex %s" % me)
        if me is S.NaN:
            raise TypeError("Invalid NaN comparison")
    n2 = _n2(self, other)
    if n2 is not None:
        return _sympify(n2 < 0)
    if self.is_extended_real and other.is_extended_real:
        if (self.is_infinite and self.is_extended_positive) \
                or (other.is_infinite and other.is_extended_negative):
            return S.false
        neg = (self - other).is_extended_negative
        if neg is not None:
            return sympify(neg)
    return StrictLessThan(self, other, evaluate=False)

def new_method_proxy(func):
    def inner(self, *args):
        if self._wrapped is empty:
            self._setup()
        return func(self._wrapped, *args)

    return inner

def identity(self):
    return super().identity + (
        self.through,
        self.through_fields,
        self.db_constraint,
    )

def make_hashable(value):
    """
    Attempt to make value hashable or raise a TypeError if it fails.

    The returned value should generate the same hash for equal values.
    """
    if isinstance(value, dict):
        return tuple([
            (key, make_hashable(nested_value))
            for key, nested_value in sorted(value.items())
        ])
    # Try hash to avoid converting a hashable iterable (e.g. string, frozenset)
    # to a tuple.
    try:
        hash(value)
    except TypeError:
        if is_iterable(value):
            return tuple(map(make_hashable, value))
        # Non-hashable, non-iterable.
        raise
    return value

def _eval_evalf(self, prec):
    base, exp = self.as_base_exp()
    base = base._evalf(prec)
    if not exp.is_Integer:
        exp = exp._evalf(prec)
    if exp.is_negative and base.is_number and base.is_real is False:
        base = base.conjugate() / (base * base.conjugate())._evalf(prec)
        exp = -exp
        return self.func(base, exp).expand()
    return self.func(base, exp)

def evalf_pow(v, prec, options):

    target_prec = prec
    base, exp = v.args

    # We handle x**n separately. This has two purposes: 1) it is much
    # faster, because we avoid calling evalf on the exponent, and 2) it
    # allows better handling of real/imaginary parts that are exactly zero
    if exp.is_Integer:
        p = exp.p
        # Exact
        if not p:
            return fone, None, prec, None
        # Exponentiation by p magnifies relative error by |p|, so the
        # base must be evaluated with increased precision if p is large
        prec += int(math.log(abs(p), 2))
        re, im, re_acc, im_acc = evalf(base, prec + 5, options)
        # Real to integer power
        if re and not im:
            return mpf_pow_int(re, p, target_prec), None, target_prec, None
        # (x*I)**n = I**n * x**n
        if im and not re:
            z = mpf_pow_int(im, p, target_prec)
            case = p % 4
            if case == 0:
                return z, None, target_prec, None
            if case == 1:
                return None, z, None, target_prec
            if case == 2:
                return mpf_neg(z), None, target_prec, None
            if case == 3:
                return None, mpf_neg(z), None, target_prec
        # Zero raised to an integer power
        if not re:
            return None, None, None, None
        # General complex number to arbitrary integer power
        re, im = libmp.mpc_pow_int((re, im), p, prec)
        # Assumes full accuracy in input
        return finalize_complex(re, im, target_prec)

    # Pure square root
    if exp is S.Half:
        xre, xim, _, _ = evalf(base, prec + 5, options)
        # General complex square root
        if xim:
            re, im = libmp.mpc_sqrt((xre or fzero, xim), prec)
            return finalize_complex(re, im, prec)
        if not xre:
            return None, None, None, None
        # Square root of a negative real number
        if mpf_lt(xre, fzero):
            return None, mpf_sqrt(mpf_neg(xre), prec), None, prec
        # Positive square root
        return mpf_sqrt(xre, prec), None, prec, None

    # We first evaluate the exponent to find its magnitude
    # This determines the working precision that must be used
    prec += 10
    yre, yim, _, _ = evalf(exp, prec, options)
    # Special cases: x**0
    if not (yre or yim):
        return fone, None, prec, None

    ysize = fastlog(yre)
    # Restart if too big
    # XXX: prec + ysize might exceed maxprec
    if ysize > 5:
        prec += ysize
        yre, yim, _, _ = evalf(exp, prec, options)

    # Pure exponential function; no need to evalf the base
    if base is S.Exp1:
        if yim:
            re, im = libmp.mpc_exp((yre or fzero, yim), prec)
            return finalize_complex(re, im, target_prec)
        return mpf_exp(yre, target_prec), None, target_prec, None

    xre, xim, _, _ = evalf(base, prec + 5, options)
    # 0**y
    if not (xre or xim):
        return None, None, None, None

    # (real ** complex) or (complex ** complex)
    if yim:
        re, im = libmp.mpc_pow(
            (xre or fzero, xim or fzero), (yre or fzero, yim),
            target_prec)
        return finalize_complex(re, im, target_prec)
    # complex ** real
    if xim:
        re, im = libmp.mpc_pow_mpf((xre or fzero, xim), yre, target_prec)
        return finalize_complex(re, im, target_prec)
    # negative ** real
    elif mpf_lt(xre, fzero):
        re, im = libmp.mpc_pow_mpf((xre, fzero), yre, target_prec)
        return finalize_complex(re, im, target_prec)
    # positive ** real
    else:
        return mpf_pow(xre, yre, target_prec), None, target_prec, None

def _eval_power(b, e):

    # don't break up NC terms: (A*B)**3 != A**3*B**3, it is A*B*A*B*A*B
    cargs, nc = b.args_cnc(split_1=False)

    if e.is_Integer:
        return Mul(*[Pow(b, e, evaluate=False) for b in cargs]) * \
            Pow(Mul._from_args(nc), e, evaluate=False)
    if e.is_Rational and e.q == 2:
        from sympy.core.power import integer_nthroot
        from sympy.functions.elementary.complexes import sign
        if b.is_imaginary:
            a = b.as_real_imag()[1]
            if a.is_Rational:
                n, d = abs(a/2).as_numer_denom()
                n, t = integer_nthroot(n, 2)
                if t:
                    d, t = integer_nthroot(d, 2)
                    if t:
                        r = sympify(n)/d
                        return _unevaluated_Mul(r**e.p, (1 + sign(a)*S.ImaginaryUnit)**e.p)

    p = Pow(b, e, evaluate=False)

    if e.is_Rational or e.is_Float:
        return p._eval_expand_power_base()

    return p

def as_relational(self, symbol):
    """Rewrite an Intersection in terms of equalities and logic operators"""
    return And(*[set.as_relational(symbol) for set in self.args])

def make_args(cls, expr):
    """
    Return a set of args such that cls(*arg_set) == expr.
    """
    if isinstance(expr, cls):
        return expr._argset
    else:
        return frozenset([sympify(expr)])

def __new__(cls, *args, **kwargs):
    evaluate = kwargs.get('evaluate', global_evaluate[0])
    if evaluate:
        args = list(map(sympify, args))

        if len(args) == 0:
            return EmptySet()
    else:
        args = list(map(sympify, args))

    args = list(ordered(set(args), Set._infimum_key))
    obj = Basic.__new__(cls, *args)
    return obj

def __iter__(self):
    no_iter = True
    for s in self.args:
        if s.is_iterable:
            no_iter = False
            other_sets = set(self.args) - set((s,))
            other = Intersection(*other_sets, evaluate=False)
            for x in s:
                c = sympify(other.contains(x))
                if c is S.true:
                    yield x
                elif c is S.false:
                    pass
                else:
                    yield c

    if no_iter:
        raise ValueError("None of the constituent sets are iterable")

def get_field_type(self, connection, table_name, row):
    """
    Given the database connection, the table name, and the cursor row
    description, this routine will return the given field type name, as
    well as any additional keyword parameters and notes for the field.
    """
    field_params = {}
    field_notes = []

    try:
        field_type = connection.introspection.get_field_type(row.type_code, row)
    except KeyError:
        field_type = "TextField"
        field_notes.append("This field type is a guess.")

    # Add max_length for all CharFields.
    if field_type == "CharField" and row.internal_size:
        field_params["max_length"] = int(row.internal_size)

    if field_type in {"CharField", "TextField"} and row.collation:
        field_params["db_collation"] = row.collation

    if field_type == "DecimalField":
        if row.precision is None or row.scale is None:
            field_notes.append(
                "max_digits and decimal_places have been guessed, as this "
                "database handles decimal fields as float"
            )
            field_params["max_digits"] = (
                row.precision if row.precision is not None else 10
            )
            field_params["decimal_places"] = (
                row.scale if row.scale is not None else 5
            )
        else:
            field_params["max_digits"] = row.precision
            field_params["decimal_places"] = row.scale

    return field_type, field_params, field_notes

def contribute_to_class(self, cls, name, private_only=False, **kwargs):

    super().contribute_to_class(cls, name, private_only=private_only, **kwargs)

    self.opts = cls._meta

    if not cls._meta.abstract:
        if self.remote_field.related_name:
            related_name = self.remote_field.related_name
        else:
            related_name = self.opts.default_related_name
        if related_name:
            related_name = related_name % {
                "class": cls.__name__.lower(),
                "model_name": cls._meta.model_name.lower(),
                "app_label": cls._meta.app_label.lower(),
            }
            self.remote_field.related_name = related_name

        if self.remote_field.related_query_name:
            related_query_name = self.remote_field.related_query_name % {
                "class": cls.__name__.lower(),
                "app_label": cls._meta.app_label.lower(),
            }
            self.remote_field.related_query_name = related_query_name

        def resolve_related_class(model, related, field):
            field.remote_field.model = related
            field.do_related_class(related, model)

        lazy_related_operation(
            resolve_related_class, cls, self.remote_field.model, field=self
        )

def database_forwards(self, app_label, schema_editor, from_state, to_state):
    to_model = to_state.apps.get_model(app_label, self.name)
    if self.allow_migrate_model(schema_editor.connection.alias, to_model):
        from_model = from_state.apps.get_model(app_label, self.name)
        # Remove a field if we need to
        if from_model._meta.order_with_respect_to and not to_model._meta.order_with_respect_to:
            schema_editor.remove_field(from_model, from_model._meta.get_field("_order"))
        # Add a field if we need to (altering the column is untouched as
        # it's likely a rename)
        elif to_model._meta.order_with_respect_to and not from_model._meta.order_with_respect_to:
            field = to_model._meta.get_field("_order")
            if not field.has_default():
                field.default = 0
            schema_editor.add_field(
                from_model,
                field,
            )

def database_forwards(self, app_label, schema_editor, from_state, to_state):
    model = to_state.apps.get_model(app_label, self.model_name)
    if self.allow_migrate_model(schema_editor.connection.alias, model):
        schema_editor.add_index(model, self.index)

def _prepare(self, model):
    if self.order_with_respect_to:
        # The app registry will not be ready at this point, so we cannot
        # use get_field().
        query = self.order_with_respect_to
        try:
            self.order_with_respect_to = next(
                f for f in self._get_fields(reverse=False)
                if f.name == query or f.attname == query
            )
        except StopIteration:
            raise FieldDoesNotExist("%s has no field named '%s'" % (self.object_name, query))

        self.ordering = ('_order',)
        if not any(isinstance(field, OrderWrt) for field in model._meta.local_fields):
            model.add_to_class('_order', OrderWrt())
    else:
        self.order_with_respect_to = None

    if self.pk is None:
        if self.parents:
            # Promote the first parent link in lieu of adding yet another
            # field.
            field = next(iter(self.parents.values()))
            # Look for a local field with the same name as the
            # first parent link. If a local field has already been
            # created, use it instead of promoting the parent
            already_created = [fld for fld in self.local_fields if fld.name == field.name]
            if already_created:
                field = already_created[0]
            field.primary_key = True
            self.setup_pk(field)
        else:
            auto = AutoField(verbose_name='ID', primary_key=True, auto_created=True)
            model.add_to_class('id', auto)

def _eval_subs(self, old, new):
    # this is strictly not necessary, but we can keep track
    # of whether True or False conditions arise and be
    # somewhat more efficient by avoiding other substitutions
    # and avoiding invalid conditions that appear after a
    # True condition
    args = list(self.args)
    args_exist = False
    for i, (e, c) in enumerate(args):
        c = c._subs(old, new)
        if c != False:
            args_exist = True
            e = e._subs(old, new)
        args[i] = (e, c)
        if c == True:
            break
    if not args_exist:
        args = ((Undefined, True),)
    return self.func(*args)

def flatten(self, args, func):
    result = []
    for arg in args:
        if isinstance(arg, ast.Call):
            arg_func = arg.func
            if isinstance(arg_func, ast.Call):
                arg_func = arg_func.func
            if arg_func.id == func:
                result.extend(self.flatten(arg.args, func))
            else:
                result.append(arg)
        else:
            result.append(arg)
    return result

def _filter_or_exclude(self, negate, *args, **kwargs):
    if args or kwargs:
        assert not self.query.is_sliced, \
            "Cannot filter a query once a slice has been taken."

    clone = self._chain()
    if self._defer_next_filter:
        self._defer_next_filter = False
        clone._deferred_filter = negate, args, kwargs
    else:
        clone._filter_or_exclude_inplace(negate, *args, **kwargs)
    return clone

def check_filterable(self, expression):
    """Raise an error if expression cannot be used in a WHERE clause."""
    if not getattr(expression, 'filterable', True):
        raise NotSupportedError(
            expression.__class__.__name__ + ' is disallowed in the filter '
            'clause.'
        )
    if hasattr(expression, 'get_source_expressions'):
        for expr in expression.get_source_expressions():
            self.check_filterable(expr)
